(ns gcs.oauth.token.jwt-encoding-decoding
  (:require [base64-clj.core :as base64]
             [clojure.string :as str]))

(defn decodeAssertion [a]
  (map base64/decode
       (drop-last
         (str/split a #"\."))))

(defn printCol [col]
  (doseq [elem col]
    (println elem)))

